import React, { Component } from 'react';

class Sidebar extends Component {
state = {
    links: [
      {
        id: 1,
        name: "Link1",
        to: "/cms",
        className: "side_nav_item"
      },
      {
        id: 2,
        name: "Link2",
        to: "/cms",
        className: "side_nav_item"
      },
      {
        id: 3,
        name: "Link3",
        to: "/cms",
        className: "side_nav_item"
      },
      {
        id: 4,
        name: "Link4",
        to: "/cms",
        className: "side_nav_item"
      }
    ],
    activeLink: null
  };

  handleClick = id => {
    this.setState({ activeLink: id });
  };
state = {
    active: 'no',
    cssclass: 'inactive'
  }
  addActiveClass = (e) =>{
      //const clicked = e.target.id;
      console.log(e);
      this.setState({active: 'yes'});
      console.log(this.state.active);
       if(this.state.active === 'no') { 
           this.setState({cssclass: 'inactive'});
       } else {
           this.setState({cssclass: 'active'});
       }
       console.log(this.state.cssclass);
  }

  render() {
    const { links, activeLink } = this.state;

    return (
      <div className="responsive-nav">
          <i className="fa fa-bars" id="menu-toggle" />
          <div id="menu" className="menu">
            <i className="fa fa-times" id="menu-close" />
            <div className="container">
              <div className="image">
                <a href="#"><img src="images/author-image.jpg" alt="" /></a>
              </div>
              <div className="author-content">
                <h4>Reflux Me</h4>
                <span>Web Designer</span>
              </div>
              <nav className="main-nav" role="navigation">
                {links.map(link => {
          return (
            <div key={link.id}>
              <ul>
                <li
                  onClick={() => this.handleClick(link.id)}
                  className={
                    link.className +
                    (link.id === activeLink ? " active_item" : "")
                  }
                >
                  {link.name} {link.id === activeLink && "active!"}
                </li>
              </ul>
            </div>
          );
        })}
              </nav>
              <div className="social-network">
                <ul className="soial-icons">
                  <li>
                    <a href="https://fb.com/templatemo"><i className="fa fa-facebook" /></a>
                  </li>
                  <li>
                    <a href="#"><i className="fa fa-twitter" /></a>
                  </li>
                  <li>
                    <a href="#"><i className="fa fa-linkedin" /></a>
                  </li>
                  <li>
                    <a href="#"><i className="fa fa-dribbble" /></a>
                  </li>
                  <li>
                    <a href="#"><i className="fa fa-rss" /></a>
                  </li>
                </ul>
              </div>
              <div className="copyright-text">
                <p>Copyright 2019 Reflux Design</p>
              </div>
            </div>
          </div>
        </div>
    );
  }
}
export default Sidebar;
